INSERT INTO json_table (id, airtableId, slug, name, workplace, description, about, competencies, experience, price, menteeCount, photo, photo_url, tags, sortOrder, is_visible, sponsors, calendarType)
SELECT id, airtableId, slug, name, workplace, description, about, competencies, experience, price, menteeCount, photo, photo_url, tags, sortOrder, is_visible, sponsors, calendarType from json l cross join lateral json_populate_recordset(null::json_table, doc) as p;


INSERT INTO users (first_name, last_name, nickname, avatar, about, is_searchable)
SELECT (string_to_array(p.name, ' '))[1], (string_to_array(p.name, ' '))[2], p.slug, p.photo_url, concat(p.about, ' ', p.description), p.is_visible
From json_table as p;
INSERT INTO skills (name)
SELECT DISTINCT UNNEST(tags) FROM json_table ON CONFLICT DO NOTHING;

INSERT INTO users_skills (user_id, skill_name) (
    SELECT u.id, UNNEST(json_table.tags) from users as u
                                                  JOIn json_table on json_table.slug = u.nickname
)
